import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormGroup,Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  type:string ="password";
  eyeIcon:string="fa-eye-slash";
  loginForm!:FormGroup;

  constructor(private fb:FormBuilder){}//in here we are injecting form builder

  ngOnInit():void{
    //in here we are grouping username and password and validating them
    //those are known as controlls username and password
    this.loginForm=this.fb.group({
      username:['',Validators.required],
      password:['',Validators.required]
    })

  }

  hideShowPass(){
    if(this.type =="password"){
      this.type="text";
      this.eyeIcon="fa-eye";
    }else{
      this.type="password";
      this.eyeIcon="fa-eye-slash";
    }

  }

  onSubmit(){
    if(this.loginForm.valid){
      //send the object to database
      console.log(this.loginForm.value);
    }else{
      //throw the error using toaster and with required field
      console.log("Form is not valid!");
    }
  }

}
