import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit{

  type:string ="password";
  eyeIcon:string="fa-eye-slash";

  constructor(){}
  ngOnInit():void{

  }

  hideShowPass(){
    if(this.type =="password"){
      this.type="text";
      this.eyeIcon="fa-eye";
    }else{
      this.type="password";
      this.eyeIcon="fa-eye-slash";
    }
  }

}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-light">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Login</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form>
                            <div class="form-group pt-3">
                                <div class="input-field">
                                        <span class="fa fa-user p-2"></span>
                                        <input type="text" placeholder="Username" class="mx-2">

                                </div>
                            </div>

                            <div class="form-group pt-3">
                                <div class="input-field">
                                        <span class="fa fa-solid fa-lock p-2"></span>
                                        <input type={{type}} placeholder="Password" class="mx-2">
                                    <span (click) = "hideShowPass()" class="fa fa-eye {{eyeIcon}}"></span>
                                </div>
                            </div>
                            <div class="form-inline fr mt-4">
                                <a href="" id="forget" class="font-weight-bold">Forget Password?</a>
                            </div>
                            <button type="submit" class="btn btn-primary btn-block mt-4 w-100">Login</button>
                            <div class="text-center pt-4 text-muted">Already have account?<a routerLink="/signup" id="foget">signup</a></div>
                        </form>
                    </div>
                    <div class="mx-3 my-2 py-3 bordert">
                        <div class="text-center py-1">
                            <a href="https://www.facebook.com" class="px-2"><img src="https://cdn2.iconfinder.com/data/icons/social-media-2285/512/1_Facebook_colored_svg_copy-1024.png" alt=""></a>
                            <a href="https://www.google.com" class="px-2"><img src="https://cdn2.iconfinder.com/data/icons/social-icons-33/128/Google-1024.png" alt=""></a>
                        <a href="https://www.github.com" class="px-2"><img src="https://cdn1.iconfinder.com/data/icons/logotypes/32/github-1024.png" alt=""></a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
